#mainpage_viewport {
  z-index: 1;
  position: relative;
  display: flex;
  justify-content: center;
  width: 100%;
  height: 100%;

  #mainpage_container {
    z-index: 1;
    position: relative;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    top: 78px;
    @include mobile() {
      width: 100%;
    }
    @include tab() {
      width: 100%;
    }
    @include desktop() {
      width: 1000px;
    }

    .title_wrapper {
      position: relative;
      display: flex;
      align-items: center;
      justify-content: center;
      height: 35px;
      width: 100%;
      margin-top: 100px;
      margin-bottom: 100px;

      > p {
        color: $dark;
        font-size: $titleFont;
      }
    }

    .posts_wrapper {
      position: relative;
      display: flex;
      flex-wrap: wrap;
      justify-content: space-between;

      @include mobile() {
        width: 250px;
      }
      @include tab() {
        width: 550px;
      }
      @include desktop() {
        width: 850px;
      }

      .card {
        @extend %cardSize;
        margin-bottom: 100px;
        cursor: pointer;

        .img_wrapper {
          width: 250px;
          height: 173px;

          > img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            object-position: center;
          }
        }

        .contents_wrapper {
          display: flex;
          flex-wrap: wrap;
          align-items: space-between;
          width: 250px;
          height: 77px;

          .title {
            display: flex;
            align-items: center;
            width: 100%;
            height: 19px;
            margin-top: 5px;
            @extend %articleTitleFont;
            color: $title;
          }
          .tutor {
            display: flex;
            align-items: center;
            width: 100%;
            height: 17px;
            font-size: $articleTutorFont;
            color: $darkgray;
          }
          .price {
            @extend .title;
            margin-top: 0px;
            margin-bottom: 5px;
            color: $blue;
          }
        }
      }
    }

    .add_button {
      position: fixed;
      display: flex;
      align-items: center;
      justify-content: center;
      width: 90px;
      height: 90px;
      border-radius: 100%;
      box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.3);
      cursor: pointer;

      @include mobile() {
        bottom: 20px;
        right: 20px;
      }
      @include tab() {
        bottom: 40px;
        right: 40px;
      }
      @include desktop() {
        bottom: 40px;
        right: 40px;
      }
      > img {
        width: 100%;
        border-radius: 100%;
      }
    }
  }
}

