#header_container {
  z-index: 2;
  position: fixed;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 78px;
  background-color: $white;
  box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 10%);

  #desktop_wrapper {
    position: relative;
    align-items: center;
    max-width: 1000px;
    width: 100%;
    height: 100%;
    @include mobile() {
      display: none;
    }
    @include tab() {
      display: none;
    }
    @include desktop() {
      display: flex;
    }
    .logo_wrapper {
      position: relative;
      width: 120px;
      margin-left: 10px;
      cursor: pointer;

      > img {
        width: 100%;
        object-fit: cover;
        object-position: center;
      }
    }

    .search_box {
      position: absolute;
      display: flex;
      align-items: center;
      width: 550px;
      height: 40px;
      left: 50%;
      transform: translateX(-50%);
      background-color: $searchBack;
      border: 1px solid $searchBorder;
      border-radius: 25px;
      box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 5%);

      > input {
        margin-left: 25px;
        height: 100%;
        width: 400px;
        background-color: transparent;
        border: none;
        color: $searchColor;
        font-size: $searchDesktopFont;
        outline: none;
      }
      input::placeholder {
        color: $searchColor;
        font-size: $searchDesktopFont;
      }

      .icon_wrapper {
        position: absolute;
        width: 20px;
        height: 20px;
        right: 25px;
        cursor: pointer;

        > img {
          width: 100%;
          opacity: 0.6;
        }
      }
    }
  }

  #mobile_wrapper {
    position: relative;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    @include mobile() {
      display: flex;
    }
    @include tab() {
      display: flex;
    }
    @include desktop() {
      display: none;
    }

    .mobile_icon_wrapper {
      position: absolute;
      width: 30px;
      height: 30px;
      left: 10px;
      cursor: pointer;

      > img {
        width: 100%;
        opacity: 0.6;
      }
    }

    .mobile_logo_wrapper {
      position: relative;
      width: 120px;
      cursor: pointer;

      > img {
        width: 100%;
        object-fit: cover;
        object-position: center;
      }
    }
  }
}
